import React from "react";
import ItemGrid from "../components/ItemGrid";
import ItemObject from "../components/ItemObject";

class ItemPage extends React.Component {
  render() {
    const itemId = this.props.match.params.id;
    const item = this.props.clothes.find((item) => item.name.includes(itemId));
    return (
      <>
        <ItemObject item={item} addingToCart={this.props.addingToCart} />
        <div className="other">
          <ItemGrid items={this.props.clothes} limit={3} />
        </div>
      </>
    );
  }
}

export default ItemPage;
