import React from "react";
import { BrowserRouter, Route } from "react-router-dom";
import HomePage from "./pages/HomePage";
import ItemPage from "./pages/ItemPage";
import CartPage from "./pages/CartPage";
import Header from "./components/Header";
import Footer from "./components/Footer";
import MailingList from "./components/MailingList";
import data from "./data/store-items.json";

class App extends React.Component {
  state = {
    clothes: data,
    cart: [],
  };

  // Add to cart
  addToCart = async (item) => {
    try {
      await this.setState((state) => {
        const newItem = state.cart.concat(item);
        return {
          cart: newItem,
        };
      });
      alert("Added item to cart! :D");
    } catch (err) {
      alert("Error :(");
    }
  };

  // Deleting

  render() {
    return (
      <BrowserRouter>
        <Header />
        <>
          {/* HOME PAGE */}
          <Route
            exact
            path="/"
            render={(props) => (
              <HomePage {...props} clothes={this.state.clothes} />
            )}
          />
          {/* SPECIFIC ITEM PAGE */}
          <Route
            exact
            path="/collection/:id"
            render={(props) => (
              <ItemPage
                {...props}
                clothes={this.state.clothes}
                addingToCart={this.addToCart}
              />
            )}
          />
          {/* CART PAGE */}
          <Route
            exact
            path="/cart"
            render={(props) => (
              <CartPage {...props} cartItems={this.state.cart} />
            )}
          />
        </>
        <MailingList />
        <Footer />
      </BrowserRouter>
    );
  }
}

export default App;
