import React from "react";
import Button from "./Button";
import SmallImg from "./SmallImg";

class ItemObject extends React.Component {
  render() {
    return (
      <div className="itemPage-container">
        <div className="small-images">
          <SmallImg item={this.props.item} />
          <SmallImg item={this.props.item} />
          <SmallImg item={this.props.item} />
        </div>
        <div>
          <img
            src={require(`../img/${this.props.item.image}`)}
            alt={this.props.item.name}
            className="big-image"
          />
        </div>
        <div className="item-desc">
          <h1 className="item-price">{this.props.item.price}</h1>
          <h1 className="item-name">{this.props.item.name}</h1>
          <p>{this.props.item.description}</p>
          <div className="colors">
            <b>Color:</b>
            <p> Pink</p>
            <div className="color-boxes">
              <div className="pink"></div>
              <div className="blue"></div>
              <div className="black"></div>
            </div>
          </div>
          <div className="add">
            <input
              type="number"
              name="counter"
              placeholder="0"
              min={1}
              className="counter"
            />
            <Button
              text="ADD TO CART"
              classes="is-primary addto"
              //Fix
              onClick={this.props.addingToCart(this.props.item)}
            />
          </div>
        </div>
      </div>
    );
  }
}

export default ItemObject;
